import styled from 'styled-components';

export const Container = styled.div`
    padding: 80px 60px;
    background: #6ca3de;
    border-radius: 2rem`

export const Wrapper = styled.div`
    display: flex;
    flex-direction: colunm;
    justify-content: center;
    max-width: 100%;
    margin: 0 auto;
`

export const Colunm = styled.div`
    display: flex;
    flex-direction: colunm;
    text-align: left;
    margin-left: 2px;
`

export const Row = styled.div`
    display: grid;
    grid-template-colunm: repeat(auto-fill, minmax(230px, 1fr));
    grid-gap: 20px;
    @media (max-width: 1000px) {
        grid-template-colunm
    }
`

export const Link = styled.div`
    color: #808080;
    margin-bottum: 20px;
    font-size: 16px;
    text-decoration: none;
    &:hover {
        color: #809090;
        transition: 200ms case-in;
    }
`

export const Title = styled.div`
    font-size: 24px;
    color: #808080;
    margin-bottum: 40px;
    font-weight: bold;
`
